    <React.Fragment>
      <header>
        <Header />
      </header>
      <main>
        <Routes>
         <Route path="/" element={<Home />} />
         <Route path="/add" element={<AddBook />} />
         <Route path="/books" element={<Books />} />
         <Route path="/about" element={<About />} />
         <Route path="/books/:id" element={<BookDetail />} />
         <Route path="/login" element={<Login />} />
        </Routes>
      </main>
    </React.Fragment>
  );
}

export default App;
